// ‚úÖ Dashboard.js
import React from 'react';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const { user } = useAuth();
  
  if (!user) return <p className="dashboard-message">Please login to access the dashboard.</p>;
  
  return (
    <div className="dashboard-container">
      <div className="dashboard-card">
        <h2>Welcome, {user.username}</h2>
        <p className="user-role">Role: <strong>{user.role}</strong></p>
        
        <div className="dashboard-buttons">
          <Link to="/view-schedule" className="dash-btn">üìÖ View Schedule</Link>
          
          {/* Book Train button for both Passenger and Staff */}
          {(user.role === 'Passenger' || user.role === 'Staff') && (
            <Link to="/book-train" className="dash-btn">üöÜ Book a Train</Link>
          )}
          
          {/* Travel History button for both Passenger and Staff */}
          {(user.role === 'Passenger' || user.role === 'Staff') && (
            <Link to="/travel-history" className="dash-btn">üìú Travel History</Link>
          )}
          
          {/* Admin-specific button */}
          {user.role === 'Admin' && (
            <Link to="/admin" className="dash-btn">‚öôÔ∏è Admin Panel</Link>
          )}
          
          {/* Staff-specific button */}
          {user.role === 'Staff' && (
            <Link to="/manage-rides" className="dash-btn">üßæ Manage Rides</Link>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
